package leetcode.thirtydays.may;

import java.util.ArrayList;
import java.util.List;

/*
 * https://leetcode.com/problems/find-all-anagrams-in-a-string/discuss/92007/Sliding-Window-algorithm-template-to-solve-all-the-Leetcode-substring-search-problem.
 */
public class Day16_FindAllAngagramsOfString {

	public static void main(String[] args) {
		// System.out.println(findAnagrams("cbaebabacd", "abc"));
		// System.out.println(findAnagrams("abab", "ab"));

		System.out.println(findAnagrams(
				"",
				""));
	}

	public static List<Integer> findAnagrams(String s, String p) {
		System.out.println(s.length());
		System.out.println(p.length());

		if (s == null || p == null || (s != null && s.length() == 0) || (p != null && p.length() == 0)) {
			return new ArrayList<Integer>();
		}
		List<Integer> list = new ArrayList<Integer>();
		int[] pArray = new int[26];
		for (int i = 0; i < p.length(); i++) {
			pArray[p.charAt(i) - 'a']++;
		}
		
		for (int i = 0; i <= s.length() - p.length(); i++) {
			String slidingString = s.substring(i, p.length() + i);
			
			int[] arr1 = new int[26];
			for (int j = 0; j < p.length(); j++) {
				arr1[slidingString.charAt(j) - 'a']++;
			}
			if (java.util.Arrays.equals(pArray, arr1))
				list.add(i);
		}
		return list;
	}

	public static List<Integer> findAnagrams1(String s, String p) {
		System.out.println(s.length());
		System.out.println(p.length());

		if (s == null || p == null || (s != null && s.length() == 0) || (p != null && p.length() == 0)) {
			return new ArrayList<Integer>();
		}

		java.util.Map<Character, Integer> map = new java.util.HashMap<>();
		for (char c : p.toCharArray()) {
			if (map.containsKey(c)) {
				map.put(c, 1 + map.get(c));
			} else {
				map.put(c, 1);
			}
		}

		java.util.List<Integer> list = new java.util.ArrayList<Integer>();

		for (int i = 0; i <= s.length() - p.length(); i++) {
			String slidingString = s.substring(i, p.length() + i);

			java.util.Map<Character, Integer> tempMap = new java.util.HashMap<>();
			for (char c : slidingString.toCharArray()) {
				if (tempMap.containsKey(c)) {
					tempMap.put(c, 1 + tempMap.get(c));
				} else {
					tempMap.put(c, 1);
				}
			}

			if (areSame(map, tempMap)) {
				list.add(i);
			}
		}
		return list;
	}

	private static boolean areSame(java.util.Map<Character, Integer> map, java.util.Map<Character, Integer> tempMap) {
		if (map.size() != tempMap.size()) {
			return false;
		}

		for (java.util.Map.Entry<Character, Integer> x : map.entrySet()) {
			if (!(tempMap.containsKey(x.getKey()) && tempMap.get(x.getKey()).equals(map.get(x.getKey())))) {
				return false;
			}
		}
		return true;
	}

	private static void makeAnagram(char[] a, int i, List<String> l) {
		if (i == a.length - 1) {
			l.add(new String(a));
		} else {
			for (int j = i; j < a.length; j++) {
				// swap a[i] with a[j]
				char c = a[i];
				a[i] = a[j];
				a[j] = c;
				makeAnagram(a, i + 1, l);
				// swap back
				c = a[i];
				a[i] = a[j];
				a[j] = c;
			}
		}
	}// end of makeAnagram
}
